CustomField.formula:
IF(
				ISPICKVAL(CorrectionRight__c,"Si")
				/*if true*/
			 ,IF(ISPICKVAL(FirstLevelComplaintClassification__c, "Reclamo Scritto")
								/*if true Reclamo Scritto*/
								,IF(CorrectionDocumentEmissionDay__c <= SendDate__c + 120 && CorrectionDocumentEmissionDay__c > SendDate__c + 60
												/*if true first SLA*/ 
												, 25
												/*if false first SLA*/
												, IF(CorrectionDocumentEmissionDay__c <= SendDate__c + 180 && CorrectionDocumentEmissionDay__c > SendDate__c + 120
																	/*if true second SLA*/
																	, 50
																	/*if false second SLA*/
																	, IF(CorrectionDocumentEmissionDay__c > SendDate__c + 180
																						/*if true third SLA*/
																						, 75
																						/*if false third SLA*/
																						,0
																					)
																	)
												)
								/*if false Reclamo Scritto*/									
								,IF(ISPICKVAL(FirstLevelComplaintClassification__c, "Rettifica Doppia Fatturazione")
												/*if true Rettifica Doppia Fatturazione*/
												,IF(CorrectionDocumentEmissionDay__c <= SendDate__c + 40 && CorrectionDocumentEmissionDay__c > SendDate__c + 20
																/*if true first SLA*/
																,25
																/*if false first SLA*/
																,IF(CorrectionDocumentEmissionDay__c <= SendDate__c + 60 && CorrectionDocumentEmissionDay__c > SendDate__c + 40
																				/*if true second SLA*/
																				,50
																				/*if false second SLA*/
																				,IF(CorrectionDocumentEmissionDay__c > SendDate__c + 60
																								/* if true third SLA*/
																								,75
																								/*if false third SLA*/
																								,0)
																			)
															)
												/*if false Rettifica Doppia Fatturazione*/
												,0)
							)
				/*if false*/
				,0
)
